import {useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
import Preformatted from "../preformatted";
import "../../index.css"


const DetailScreen = () => {
    const [drinkDetails, setDrinkDetails] = useState({})
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?'
    const imdbID = 11007;
    //const {imdbID} = useParams()
    const searchDrinkByID = async () => {
        const response = await axios.get(`${url}i=${imdbID}`)
        setDrinkDetails(response.data)
    }
    useEffect( () => {
        searchDrinkByID()}, []
    )
    return(
        <>
            <h1 className="wd-color-orange">Welcome to Detail Screen</h1>
            <div>
                <div>
                    <h4><span><i className="wd-blue-color fa-solid fa-circle-info"></i>&nbsp;</span>This is information from remote API</h4>
                    <h1 className="pt-3 pb-3">{drinkDetails.drinks[0].strDrink}<span>&nbsp;
                        <i className="wd-yellow-color fa-solid fa-martini-glass-citrus"></i>
                    </span></h1>

                    <img src={drinkDetails.drinks[0].strDrinkThumb} height={300} width={300}/>

                    <ul className="mt-2">
                        <h5 className="wd-bolded-font">
                            Category
                        </h5>
                        <li className="wd-font pb-4">
                            {drinkDetails.drinks[0].strCategory}
                        </li>
                        <h5 className="wd-bolded-font">
                            Alcoholic
                        </h5>
                        <li className="wd-font pb-4">
                            {drinkDetails.drinks[0].strAlcoholic}
                        </li>
                        <h5 className="wd-bolded-font">
                            Instructions
                        </h5>
                        <li className="wd-font pb-4">
                            {drinkDetails.drinks[0].strInstructions}
                        </li>
                    </ul>


                    <Preformatted obj={drinkDetails}/>
                </div>


                <div>
                    <h2>info from local api</h2>
                </div>

            </div>
        </>

    )

};
export default DetailScreen;
