import {useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
import Preformatted from "../preformatted";
import "../../index.css"


const DetailScreen = () => {
    const [drinkDetails, setDrinkDetails] = useState({})
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?'
    const imdbID = 11007;
    //const {imdbID} = useParams()
    const searchDrinkByID = async () => {
        const response = await axios.get(`${url}i=${imdbID}`)
        setDrinkDetails(response.data)
    }
    useEffect( () => {
        searchDrinkByID()}, []
    )
    return(
        <>
            <h1>Detail Screen</h1>
            <div>
                <div>
                    <h4>This is information from remote API</h4>
                    <h1 className="pt-3">{drinkDetails.drinks[0].strDrink}</h1>

                        <div>
                            {drinkDetails.drinks[0].strCategory}<i className="fa-solid fa-martini-glass-citrus"></i>
                        </div>
                        <div>
                            {drinkDetails.drinks[0].strAlcoholic}
                        </div>
                        <p>
                            {drinkDetails.drinks[0].strInstructions}
                        </p>

                    <Preformatted obj={drinkDetails}/>
                </div>


                <div>
                    <h2>info from local api</h2>
                </div>

            </div>
        </>

    )

};
export default DetailScreen;
