import {useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
import Preformatted from "../preformatted";
import "../../index.css"


const DetailScreen = () => {
    const [drinkDetails, setDrinkDetails] = useState({
        drinks: []
    })
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?'
    const imdbID = 11007;
    //const {imdbID} = useParams()
    const searchDrinkByID = async () => {
        const response = await axios.get(`${url}i=${imdbID}`)
        setDrinkDetails(response.data)
    }
    useEffect( () => {
        searchDrinkByID()}, []
    )
    return(
        <>
            <h1 className="pt-3 wd-color-orange">Welcome to Detail Screen</h1>
            <div>
                <div>
                    <h6><span><i className="wd-blue-color fa-solid fa-circle-info"></i>&nbsp;</span>This is information from remote API</h6>
                    <h1 className="wd-yellow-color pt-3 pb-3">{drinkDetails && drinkDetails.drinks[0].strDrink}<span>&nbsp;
                        <i className="wd-yellow-color fa-solid fa-martini-glass-citrus"></i>
                    </span></h1>
                    <div className="row">
                        <img className="col-4" src={drinkDetails.drinks[0].strDrinkThumb} height={350} width={300}/>

                        <ul className="col-8 ps-5">
                            <h5 className="wd-bolded-font">
                                Category
                            </h5>
                            <li className="wd-font pb-4">
                                {drinkDetails.drinks[0].strCategory}
                            </li>
                            <h5 className="wd-bolded-font">
                                Alcoholic
                            </h5>
                            <li className="wd-font pb-4">
                                {drinkDetails.drinks[0].strAlcoholic}
                            </li>
                            <h5 className="wd-bolded-font">
                                Ingredient
                            </h5>
                            <li className="wd-font pb-4">
                                {drinkDetails.drinks[0].strIngredient1}
                            </li>
                            <h5 className="wd-bolded-font">
                                Instructions
                            </h5>
                            <li className="wd-font pb-4">
                                {drinkDetails.drinks[0].strInstructions}
                            </li>
                        </ul>

                    </div>

                    <Preformatted obj={drinkDetails}/>
                </div>
                <br/>
                <hr/>



                <div>
                    <h6><span><i className="wd-blue-color fa-solid fa-circle-info"></i>&nbsp;</span>This is information from local API</h6>
                </div>

            </div>
        </>

    )

};
export default DetailScreen;
